import logo from './logo.svg';
import './App.css';

import React, { useState } from "react";

const App = () => {
  const [color, setColor] = useState("#000000");
  const [downloadType, setDownloadType] = useState("SVG");

  const handleColorChange = (e) => {
    setColor(e.target.value);
  };

  const handleDownloadTypeChange = (e) => {
    setDownloadType(e.target.value);
  };

  const handleDownload = () => {
    let duck = document.getElementById("duck");

    if (downloadType === "SVG") {
      let svgData = duck.outerHTML;
      let svgBlob = new Blob([svgData], {
        type: "image/svg+xml;charset=utf-8",
      });
      let svgUrl = URL.createObjectURL(svgBlob);
      let downloadLink = document.createElement("a");
      downloadLink.href = svgUrl;
      downloadLink.download = "duck.svg";
      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
    } else {
      let canvas = document.createElement("canvas");
      canvas.width = duck.width.baseVal.value;
      canvas.height = duck.height.baseVal.value;
      let ctx = canvas.getContext("2d");
      ctx.drawImage(duck, 0, 0);
      let pngUrl = canvas
        .toDataURL("image/png")
        .replace("image/png", "image/octet-stream");
      let downloadLink = document.createElement("a");
      downloadLink.href = pngUrl;
      downloadLink.download = "duck.png";
      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
    }
  };

  return (
    <div>
      <div style={{ display: "flex", flexDirection: "column" }}>
        <label>
          Color:
          <input type="color" value={color} onChange={handleColorChange} />
        </label>
        <label>
          Download as:
          <select value={downloadType} onChange={handleDownloadTypeChange}>
            <option value="SVG">SVG</option>
            <option value="PNG">PNG</option>
          </select>
        </label>
        <button onClick={handleDownload}>Download</button>
      </div>
      <svg id="duck" viewBox="0 0 128 128" style={{ fill: color }}>
        <svg id="duck" viewBox="0 0 128 128" style={{ fill: color }}>
  <rect x="16" y="64" width="96" height="64"/>
  <rect x="32" y="32" width="64" height="32"/>
  <rect x="48" y="16" width="32" height="16"/>
  <rect x="56" y="0" width="16" height="16"/>
  <rect x="0" y="80" width="16" height="16" transform="rotate(-45, 8, 88)" />
  <rect x="112" y="80" width="16" height="16" transform="rotate(45, 120, 88)" />
  <rect x="40" y="96" width="48" height="16"/>
  <rect x="24" y="112" width="80" height="16"/>
</svg>

      </svg>
    </div>
  );
};

export default App;
