import logo from './logo.svg';
import './App.css';

import React, { useState } from "react";
import { CirclePicker } from "react-color";
import { SimpleGrid, Box, Card, CardBody, Heading, Stack, StackDivider, CardHeader, Text } from "@chakra-ui/react";

const App = () => {
  const [color, setColor] = useState("#FAD02C");
  const [bgColor, setBgColor] = useState("#68BBE3");
  const [eyeColor, setEyeColor] = useState("#000000");
  const [beakColor, setBeakColor] = useState("#E7625F");

    return (
      <SimpleGrid>
        <Box>

        </Box>
        <Box>
          <Card>
            <CardBody>
              <Stack divider={<StackDivider />} spacing="4">
                <Box>
                  <Heading size="xs">
                    Body Colour
                  </Heading>
                  <CirclePicker
                    color={color}
                    onChange={(color) => setColor(color.hex)}
                  />
                </Box>
                <Box>
                  <Heading size="xs">
                    Beak Colour
                  </Heading>
                  <CirclePicker
                    color={beakColor}
                    onChange={(beakColor) => setBeakColor(beakColor.hex)}
                  />
                </Box>
                <Box>
                  <Heading size="xs">
                    Eye Colour
                  </Heading>
                  <CirclePicker
                    color={eyeColor}
                    onChange={(eyeColor) => setEyeColor(eyeColor.hex)}
                  />
                </Box>
                <Box>
                  <Heading size="xs">
                    Background Colour
                  </Heading>
                  <CirclePicker
                    color={bgColor}
                    onChange={(bgColor) => setBgColor(bgColor.hex)}
                  />
                </Box>
              </Stack>
            </CardBody>
          </Card>
        </Box>
      </SimpleGrid>
    );
};

export default App;
